import {useSelector,useDispatch} from 'react-redux';
import {client} from '../../../index';
import {CREATE_USER,LOGIN_USER,GEN_OTP} from '../../../graphql/user';
import {setUsers,setLoading,setErrors, setOTP} from '../../features/user/userSlice';

export function useCreateUser(){
    const dispatch = useDispatch();
    const createUser = async (data) => {
        try 
        {
            dispatch(setLoading(true))
            const res = await client.mutate({
                mutation : CREATE_USER,
                variables : {
                    createInput : data
                }
            });

            if(res)
            {
                dispatch(setLoading(false));
                localStorage.setItem("token", res.data.createUser?.token);
            }
        } 
        catch (error) 
        {
            dispatch(setLoading(false));
            dispatch(setErrors(error.message));
        }
    }
    return{
        createUser
    }
}

export function useSendOtp(){
    const dispatch = useDispatch();
    const sendOtp = async (number) => {
        try 
        {
            dispatch(setLoading(true));
            const res = await client.mutate({
                mutation : GEN_OTP,
                variables : {
                    mobilenumber : number
                }
            });

            if(res?.data?.genOtp.otp)
            {
                dispatch(setLoading(false));
                dispatch(setOTP(res?.data?.genOtp.otp))
            }

            if(res?.data?.genOtp.args)
            {
                dispatch(setLoading(false));
                dispatch(setErrors(res?.data?.genOtp.args))
            }
        } 
        catch (error) 
        {
            dispatch(setLoading(false));
            dispatch(setErrors(error.message));
        }
    } 
    return{
        sendOtp
    }
}
