import React from "react";
import "../../assets/css/singleUser.css";
import { useNavigate } from "react-router-dom";
import moment from "moment";

const SingleUser = ({ singleuser }) => {
  const navigate = useNavigate();

  function formatTimestamp(createdAt) {
    const createdMoment = moment(createdAt); // Specify the timestamp format
    const now = moment();

    const duration = moment.duration(now.diff(createdMoment));
    console.log('duration', duration)
    if (duration.asYears() >= 1) {
      return `${Math.floor(duration.asYears())} year${duration.asYears() > 1 ? 's' : ''}`;
    } else if (duration.asMonths() >= 1) {
      return `${Math.floor(duration.asMonths())} month${duration.asMonths() > 1 ? 's' : ''}`;
    } else if (duration.asDays() >= 1) {
      return `${Math.floor(duration.asDays())} day${duration.asDays() > 1 ? 's' : ''}`;
    } else if (duration.asHours() >= 1) {
      return `${Math.floor(duration.asHours())} hr${duration.asHours() > 1 ? 's' : ''}`;
    } else if (duration.asMinutes() >= 1) {
      return `${Math.floor(duration.asMinutes())} mint${duration.asMinutes() > 1 ? 's' : ''}`;
    } else {
      return `${Math.floor(duration.asSeconds())} sec${duration.asSeconds() > 1 ? 's' : ''}`;
    }
  }

  const formattedTimestamp = formatTimestamp(singleuser.createdAt);
  console.log(formattedTimestamp);

  return (
    <div className="d-flex flex-wrap Single mt-4">
      <div>
        <img src={singleuser.avatarImage} alt={singleuser.name} height={40} />
      </div>
      <div
        className="d-flex flex-column ms-2 singleUser justify-content-center mt-1"
        onClick={() => {
          navigate(`/users/${singleuser._id}`);
        }}
      >
        <p className="mb-0">
          <b>{singleuser?.name}</b>
        </p>
        <p>Created 2 days ago</p>
      </div>
    </div>
  );
};

export default SingleUser;
