import React from "react";
import "../../assets/css/singleUser.css";
import { useNavigate } from "react-router-dom";
const SingleUser = ({ singleuser }) => {
  const navigate = useNavigate();

  function formatTimestamp(createdAt) {
    if (!createdAt || isNaN(createdAt)) {
      return "Invalid timestamp";
    }
    const now = new Date();
    const createdDate = new Date(createdAt);
    console.log('now', createdDate)
    const timeDifference = now - createdDate;

    const seconds = Math.floor(timeDifference / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);
    const months = Math.floor(days / 30);
    const years = Math.floor(months / 12);

    if (years >= 1) {
      return `${years} year${years > 1 ? "s" : ""}`;
    } else if (months >= 1) {
      return `${months} month${months > 1 ? "s" : ""}`;
    } else if (days >= 1) {
      return `${days} day${days > 1 ? "s" : ""}`;
    } else if (hours >= 1) {
      return `${hours} hr${hours > 1 ? "s" : ""}`;
    } else if (minutes >= 1) {
      return `${minutes} mint${minutes > 1 ? "s" : ""}`;
    } else {
      return `${seconds} sec${seconds > 1 ? "s" : ""}`;
    }
  }

  const formattedTimestamp = formatTimestamp(singleuser.createdAt);
  console.log(formattedTimestamp);

  return (
    <div className="d-flex flex-wrap Single mt-4">
      <div>
        <img src={singleuser.avatarImage} alt={singleuser.name} height={40} />
      </div>
      <div
        className="d-flex flex-column ms-2 singleUser justify-content-center mt-1"
        onClick={() => {
          navigate(`/users/${singleuser._id}`);
        }}
      >
        <p className="mb-0">
          <b>{singleuser?.name}</b>
        </p>
        <p>Created 2 days ago</p>
      </div>
    </div>
  );
};

export default SingleUser;
