import React from "react";
import "../../assets/css/OtherTag.css";
import { useNavigate } from "react-router-dom";
const SingleTag = ({ singleTag }) => {
  console.log("singleTag", singleTag);
  const navigate = useNavigate();

  function truncateTextToWords(text, wordCount) {
    const words = text.split(' ');
    const truncatedText = words.slice(0, 50).join(' ');
    return truncatedText;
  }

  const originalText = "MERN is an acronym for a modern technology stack consisting of MongoDB, Express.js, React, and Node.js. It is widely used for developing web applications that are robust, scalable, and efficient. MongoDB serves as the database, Express.js handles server-side operations, React powers the user interface, and Node.js ensures seamless communication between the back-end and front-end components.";

  const truncatedText = truncateTextToWords(originalText, 50);

  console.log(truncatedText);


  return (
    <div className="singleTag m-lg-2 mt-3 me-3 ps-2 pe-2 pt-2 pb-2 d-flex flex-column">
      <div
        className="singleTagBox"
        onClick={() => {
          navigate("/tags/javascript");
        }}
      >
        <p className="ps-2 pe-2 pt-1 pb-1 mb-0">{singleTag?.TagName}</p>
      </div>
      <div className="singleText mt-2">
        <p className="mb-0">
          For questions about programming in ECMAScript (JavaScript/JS) and its
          different dialects/implementations (except for ActionScript). Note
          that...
        </p>
      </div>
      <div className="singleQuestionTag mt-2">
        <b>{singleTag?.question?.length}</b> questions
      </div>
    </div>
  );
};

export default SingleTag;
