                <p className="ps-2 pe-2 pt-1 mb-0 pb-1">django</p>
              </div>
              <div className="boxSingleTag1 d-flex align-items-center">
                <p className="ps-2 pe-2 pt-1 mb-0 pb-1">django</p>
              </div>
        </div>
      </div>
    </div>
  );
};

