import {useSelector,useDispatch} from 'react-redux';
import {client} from '../../../index';
// eslint-disable-next-line
import {CREATE_USER,LOGIN_USER,GEN_OTP} from '../../../graphql/user';
// eslint-disable-next-line
import {setUsers,setLoading,setErrors, setOTP} from '../../features/user/userSlice';
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export function useCreateUser(){
    const dispatch = useDispatch();
    const userSelector = useSelector((state)=>state.user);
    const createUser = async (data) => {
        try 
        {
            dispatch(setLoading(true))
            const res = await client.mutate({
                mutation : CREATE_USER,
                variables : {
                    createInput : data
                }
            });

            if(res.data.createUser?.token)
            {
                dispatch(setLoading(false));
                localStorage.setItem("token", res.data.createUser?.token);
            }

            if(res.data.createUser?.args)
            {
                dispatch(setLoading(false));
                dispatch(setErrors(res.data.createUser?.args));
                toast.error(createInput.mobilenumber + " does not exist", {
                    position: toast.POSITION.TOP_RIGHT,
                  });
            }
        } 
        catch (error) 
        {
            dispatch(setLoading(false));
            dispatch(setErrors(error.message));
        }
    }
    return{
        createUser,
        userSelector
    }
}

export function useSendOtp(){
    const dispatch = useDispatch();
    const userSelectorOtp = useSelector((state)=>state.user);
    const sendOtp = async (number) => {
        try 
        {
            dispatch(setLoading(true));
            const res = await client.mutate({
                mutation : GEN_OTP,
                variables : {
                    mobilenumber : number
                }
            });

            if(res?.data?.genOtp.otp)
            {
                dispatch(setLoading(false));
                dispatch(setOTP(res?.data?.genOtp.otp))
            }

            if(res?.data?.genOtp.args)
            {
                dispatch(setLoading(false));
                dispatch(setErrors(res?.data?.genOtp.message))
            }
        } 
        catch (error) 
        {
            dispatch(setLoading(false));
            dispatch(setErrors(error.message));
        }
    } 
    return{
        sendOtp,
        userSelectorOtp
    }
}
