import React from "react";
import "../assets/css/signup.css";
import nodemailer from "nodemailer";

const Signup = () => {
  const transporter = nodemailer.createTransport({
    service: "gmail",
    auth: {
      user: "vanshamaggarwal697@gmail.com",
      pass: "llbkksttrjutblay",
    },
  });

  var mailOptions = {
    from: "vanshamaggarwal697@gmail.com",
    to: "vanshamaggarwal6@gmail.com",
    subject: "youtube tut",
    text: "hello there",
  };

  const sendMail = () => {
    transporter.sendMail(mailOptions, function (err, info) {
      if (err) {
        console.log(err);
      } else {
        console.log("mail sent successfully");
      }
    });
  };

  return (
    <div className="signUp position-relative">
      <div className="position-absolute signUpBg d-flex justify-content-center align-items-center">
        <div className="mainSignUp position-relative d-flex flex-column">
          <img
            src={require("../assets/image/signupTopLeft.png")}
            className="img-fluid topLeft position-absolute"
            alt="signup Top Left"
          />
          <div className="d-flex flex-column align-items-center">
            <img
              src={require("../assets/image/logo.png")}
              className="img-fluid mt-5"
              width={"100"}
              alt="Query Overflow"
            />
            <div className="d-flex pt-3 queryHeader">
              <p className="fs-3 mb-1">Query</p>
              <p className="fs-3 ms-2 fw-bold mb-1">Overflow</p>
            </div>
            <p className="m-0 pt-1 queryPara">
              Letâ€™s get you signed up and straight to the queries
            </p>
          </div>
          <div className="d-flex justify-content-start align-items-start ms-5">
            <input
              type={"text"}
              name="name"
              className="mt-5 ms-4 signupInp1 ps-2"
              placeholder="Enter your name*"
            />
          </div>
          <div className="d-flex justify-content-start align-items-center ms-5 mt-3">
            <input
              type={"text"}
              name="name"
              className="ms-4 signupInp2 ps-2"
              placeholder="Enter email*"
            />
            <button
              className="ms-4 veriButton text-light border-0 rounded-3"
              onClick={sendMail}
            >
              Send Code
            </button>
          </div>
          <div className="d-flex justify-content-start align-items-center ms-5 mt-3">
            <input
              type={"text"}
              name="name"
              className=" ms-4 signupInp1 ps-2"
              placeholder="Enter code*"
            />
            <button className="ms-4 veriButton text-light border-0 rounded-3">
              Verify Code
            </button>
          </div>
          <div className="d-flex justify-content-start align-items-start ms-5">
            <input
              type={"text"}
              name="name"
              className="mt-3 ms-4 signupInp1 ps-2"
              placeholder="Password*"
            />
          </div>
          <div className="d-flex justify-content-center align-items-center mt-4 mb-5">
            <button className="queryButton text-light border-0">Sign Up</button>
          </div>
          <img
            src={require("../assets/image/signupBottomRight.png")}
            className="img-fluid bottomRight position-absolute"
            alt="signup Bottom Right"
          />
        </div>
      </div>
    </div>
  );
};

export default Signup;
